  let(:valid_attributes) do
    FactoryGirl.attributes_for(:calouro)
  end

  let(:invalid_attributes) do
    FactoryGirl.attributes_for(:calouro, :calouro_invalid)
  end

  let(:new_attributes) do
    FactoryGirl.attributes_for(:calouro, :new_calouro)
