      calouro = Calouro.create! valid_attributes
      get :index, {}, valid_session
      expect(assigns(:calouros)).to eq([calouro])
    end
  end

  describe "GET #show" do
    it "assigns the requested calouro as @calouro" do
      calouro = Calouro.create! valid_attributes
      get :show, {:id => calouro.to_param}, valid_session
      expect(assigns(:calouro)).to eq(calouro)
    end
  end

  describe "GET #new" do
    it "assigns a new calouro as @calouro" do
